export const initialState = {
    details: [],
    basket: []
}

function reducer(state, action) {
    console.log(basket.length);
    switch(action.type){
        case 'SEND_PRODUCT':
            return {
                ...state,
                details: [action.item]
            }
        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: [...state.basket, action.item]
            }
        default:
            return state;
    }
        
}

export default reducer;
