export const initialState = {
    details: []
}

function reducer(state, action) {
    console.log(initialState.title);
    switch(action.type){
        case 'SEND_PRODUCT':
            return {
                ...state,
                details: [...state.details, action.item]
            }
        default:
            return state;
    }
        
}

export default reducer;
