import React from "react";
import saveMessage from "./Firebase";
import "./Order.css";
import { useStateValue } from "./StateProvider";
import { Link } from "react-router-dom";

function Order() {
  const [{ basket }, dispatch] = useStateValue();

  const sendOrder = () => {
    var firstName = getInputValue("firstName");
    var lastName = getInputValue("lastName");
    var email = getInputValue("email");
    var address = getInputValue("address");
    var city = getInputValue("city");
    var zipCode = getInputValue("zipCode");
    var phone = getInputValue("phone");
    var items = "";
    basket.map((i) => (items += i.title + ": " + i.quantity + "qty, "));

    dispatch({
      type: "CLEAR_BASKET",
    });

    if (
      (firstName && lastName && email && address && city && zipCode && phone)
        .length <= 0
    ) {
      document.querySelector(".order__address__left__error").style.display =
        "block";
    } else if (email.indexOf("@") === -1) {
      document.querySelector("#email").style.border = "1px solid red";
      document.querySelector(".order__address__left__error").style.display =
        "none";
    } else if (zipCode.indexOf("-") === -1) {
      document.querySelector("#email").style.border = "1px solid gray";
      document.querySelector("#zipCode").style.border = "1px solid red";
    } else if (phone.length !== 9) {
      document.querySelector("#zipCode").style.border = "1px solid gray";
      document.querySelector("#phone").style.border = "1px solid red";
    } else {
      document.querySelector("#phone").style.border = "1px solid gray";
      document.querySelector(".order__alert").style.display = "flex";
      document.querySelector(".order__block").style.display = "block";
      saveMessage(
        firstName,
        lastName,
        email,
        address,
        city,
        zipCode,
        phone,
        items
      );
    }
  };

  const getInputValue = (id) => {
    return document.getElementById(id).value;
  };

  return (
    <div className="order">
      <div className="order__block"></div>
      <div className="order__alert">
        <p>Your items has been ordered!</p>
        <Link to="/store">
          <button className="order__alert__ok">OK</button>
        </Link>
      </div>
      <div className="order__address">
        <div className="order__address__left">
          <h1>Delivery adress</h1>
          <hr className="order__separator" />
          <p className="order__address__left__error">Some fields are empty!</p>
          <label>First name: </label>
          <input type="text" id="firstName" placeholder="Json" />
          <label>Last name: </label>
          <input type="text" id="lastName" placeholder="Smith" />
          <label>E-mail: </label>
          <input type="email" id="email" placeholder="json@smith.com" />
          <label>Address: </label>
          <input type="text" id="address" placeholder="Street 5" />
          <label>City: </label>
          <input type="text" id="city" placeholder="Warsaw" />
          <label>Zip code: </label>
          <input type="text" id="zipCode" placeholder="12-345" />
          <label>Mobile phone: </label>
          <input type="tel" id="phone" placeholder="123456789" />
          <button onClick={sendOrder}>Order</button>
        </div>
        <div className="order__address__right">
          <form>
            {/* <div className='order__address__right__inpost'>
                            <label>Inpost</label> 
                            <input type='radio' name='delivery'/>
                        </div>
                        <div className='order__address__right__courier'>
                            <label>Courier</label>
                            <input type='radio' name='delivery'/> 
                        </div> */}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Order;
