import React from 'react'
import CheckoutProduct from './CheckoutProduct';
import { useStateValue } from './StateProvider'

function Checkout() {
    const [{ basket }] = useStateValue();
    return (
        <div className='checkout'>
            <div className='checkout__left'>
                {/* {basket.map(item => (
                    <CheckoutProduct
                        id={item.id}
                        title={item.title}
                        image={item.image}
                        price={item.price}
                        availble={item.availble}
                        quantity={basket.length}
                    />
                ))} */}
                <CheckoutProduct
                        id='{item.id}'
                        title='BetterComb Deep Frame 10 Pack'
                        image='https://www.betterbee.com/images/adfbcomb_n.jpg'
                        price='50'
                        availble={true}
                        quantity={5}
                    />
            </div>
        </div>
    )
}

export default Checkout
