export const initialState = {
    details: [],
    basket: [],
    multiplier: 1
}

function reducer(state, action) {
    switch(action.type){
        case 'SEND_PRODUCT':
            return {
                ...state,
                details: [action.item]
            }
        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: [...state.basket, multiplier*action.item]
            }
        default:
            return state;
    }
        
}

export default reducer;
