import React, { useState } from 'react'
import { useStateValue } from './StateProvider';
import './ProductDetails.css'

function ProductDetails() {

    const [{ details }] = useStateValue();
    const [multiplier, increaseMuliplier] = useState(1);

    return (
        <div className='details'>
            {details.map(item => (
                <div className='details__card'>
                    <img
                    className='details__img'
                    src={item.image}
                    alt=''
                />
                <div className='details__info'>
                    <h3 className='details__title'>
                        {item.title}
                    </h3>
                    <label>Quantity: </label>
                    <input className='details__quantity' onChange={event => increaseMuliplier(event.target.value)} type='number' />
                    <p className='details__price'>
                        Price: ${item.price*multiplier}
                    </p>
                    
                    <button className='details__button'>Add To Basket</button>
                </div>
                <div className='details__description'>
            This beehive winter wrap has velcro closures for a snug fit on 10 frame hives. Made from marine-grade vinyl-coated polyester, it has 1/2" thick R4 rigid Owens-Corning insulating foam, which is impervious to mice and snakes. This hive wrap helps retain the heat of the bees' cluster, keeping the colony warmer in cold northern temps. Made in the USA. Inside dimensions: 16-1/2" wide by 20" long by 17" tall. Covers two 9.5" deeps with room for the telescoping cover. Hive not included.
            </div>
            </div>
            
              ))}
            
        </div>
    )
}

export default ProductDetails
