import firebase from "firebase";

const firebaseApp = firebase.initializeApp({
  apiKey: "AIzaSyBp4eS8uvmSd0cxM39ilXL_atnOb2DygO0",
  authDomain: "wood-products-fcf18.firebaseapp.com",
  databaseURL: "https://wood-products-fcf18.firebaseio.com",
  projectId: "wood-products-fcf18",
  storageBucket: "wood-products-fcf18.appspot.com",
  messagingSenderId: "161203416501",
  appId: "1:161203416501:web:c610bf069015194e817dfc",
  measurementId: "G-NF6KEXVCT1",
});

var messagesRef = firebase.database().ref("orders");

const saveMessage = (
  firstName,
  lastName,
  email,
  address,
  city,
  zipCode,
  phone,
  items
) => {
  var newMessageRef = messagesRef.push();
  newMessageRef.set({
    firstName,
    lastName,
    email,
    address,
    city,
    zipCode,
    phone,
    items,
  });
};

export default saveMessage;
