import React from 'react'
import "./Product.css"
import { Link } from 'react-router-dom';
import { useStateValue } from "C:/Users/eryko/bee-site/src/StateProvider.js";


function Product({ id, title, image, price, availble}) {
    const [, dispatch] = useStateValue();

    const send = () => {
        dispatch({
            type: 'SEND_PRODUCT',
            item: {
                id,
                title,
                image,
                price,
                availble
            }
        })
    };

    return (
        <Link to='/product/details' onClick={send}>
            <div className='product'>
                <div className='product__img'>
                    <img
                        src={image}
                        alt=''
                    />
                </div>
                
                <h3 className='product__title'>
                    {title}
                </h3>
                <p className='product__price'>
                    Price: ${price}
                </p>
                {availble ? <h2 className='product__avail'>Availble</h2> : <h2 className='product__unavail'>Unavailable</h2>}
                {!availble ? <p>Extra time of realization</p> : <p>Normal time of realization</p>}
                
            </div>
        </Link>
        
        
    )
}

export default Product
