import React, { useState } from 'react'
import { useStateValue } from './StateProvider';
import './ProductDetails.css'
import { Link } from 'react-router-dom'
import './Store.css'

 function ProductDetails() {
    const qty = document.querySelector('.details__quantity');
    const [{ details }, dispatch] = useStateValue();
    const [multiplier, iM] = useState(0);

    const onChange = (e) => {
        if(e.keyCode < 48 || e.keyCode > 57)
        {
             qty.value = '';
        }
        else if(parseInt(qty.value, 10)>100)
        {
            qty.value = 100;
        }
    }
    if(qty)
    {
        qty.addEventListener('keydown', onChange);
    }
    
    // const comment = () => {

    //     document.querySelector('.store__alert').stylePlayState = 'running';
    // }

    const addToBasket = () => {
        document.querySelector('.details__alert').style.display = 'flex';
        document.querySelector('.details__block').style.display = 'block';
        details.map(item => (   
            dispatch({
                type: 'ADD_TO_BASKET',
                item: {
                        id: item.id,
                        title: item.title,
                        image: item.image,
                        price: Math.ceil(item.price*multiplier),
                        availble: item.availble,
                        quantity: multiplier
                    }})))}
    let increaseMultiplier = (event) => {
        iM(event.target.value);
        
    }

    
    
    return (
        
        <div className='details'>
            <div className='details__block'></div>
            <div className='details__alert'>
                <p>Item(s) added to cart!</p>
                <div className='details__alert__buttons'>
                    <Link to='/store'><button className='details__alert__buttons__store'>Back to store</button></Link>
                    <Link to='/checkout'><button className='details__alert__buttons__basket'>Go to basket</button></Link>
                </div>
                
            </div>
            {details.map(item => (
                <div className='details__card'>
                    <img
                        className='details__img'
                        src={item.image}
                        alt=''
                    />
                    <div className='details__info'>
                        <h3 className='details__title'>
                            {item.title}
                        </h3>
                        <label>Quantity: </label>
                        <input className='details__quantity' onChange={increaseMultiplier} type='number' min='1' max='100'/>
                        <p className='details__price'>
                            Price: ${item.price*multiplier}
                        </p>
                        {multiplier===0 ? <button className='details__buttonOff'>Add To Basket</button> : <button className='details__button' onClick={addToBasket}>Add To Basket</button>}
                    </div>
                </div>
              ))}
              {details.length!==0 ? 
                <div className='details__description'>
                    <p>
                        This beehive winter wrap has velcro closures for a snug fit on 10 frame hives. Made from marine-grade vinyl-coated polyester, it has 1/2" thick R4 rigid Owens-Corning insulating foam, which is impervious to mice and snakes.<br/><br/> This hive wrap helps retain the heat of the bees' cluster, keeping the colony warmer in cold northern temps. Made in the USA. Inside dimensions: 16-1/2" wide by 20" long by 17" tall. Covers two 9.5" deeps with room for the telescoping cover.<br/><br/> Hive not included.
                    </p>
                </div>
                :   
                <div className='details__description'>
                    <Link to='/store'>
                        <button>Back to store</button>
                    </Link>
                 </div>}
        </div>
    )
}

export default ProductDetails
