import React from 'react'
import "./Product.css"
import { Link } from 'react-router-dom';
import { useStateValue } from "C:/Users/eryko/wood-products/src/StateProvider.js";


function Product({ id, title, image, price, availble }) {
    const [, dispatch] = useStateValue();

    const send = () => {
        dispatch({
            type: 'SEND_PRODUCT',
            item: {
                id,
                title,
                image,
                price,
                availble
            }
        })
    };

    return (
        <Link to='/product/details' onClick={send}>
            <div className='product'>
                <div className='product__img'>
                    <img
                        src={image}
                        alt=''
                    />
                </div>

                <h3 className='product__title'>
                    {title}
                </h3>
                <p className='product__price'>
                    Cena: {price % 0.1 ? '0' : ''}zł
                </p>
                {availble ? <h2 className='product__avail'>Dostępny</h2> : <h2 className='product__unavail'>Niedostępny</h2>}
                {!availble ? <p>Dodatkowy czas realizacji</p> : <p>Normalny czas realizacji</p>}

            </div>
        </Link>


    )
}

export default Product
