export const initialState = {
    details: []
}

function reducer(state, action) {
    console.log(initialState.title);
    switch(action.type){
        case 'SEND_PRODUCT':
            return {
                ...state,
                id: action.id,
                title: action.title,
                image: action.image,
                price: action.price
            }
        default:
            return state;
    }
        
}

export default reducer;
