import React from 'react'
import { useStateValue } from './StateProvider'
import './CheckoutProduct.css'

function CheckoutProduct({id, title, image, price, availble, quantity}) {

    const [{ basket }, dispatch] = useStateValue();

    const removeFromBasket = () => {
        dispatch({
            type: 'REMOVE_FROM_BUSKET',
            id
        })
    }

    return (
        <div className='checkoutProduct'>
            <div>
            <img
                src={image}
                alt=''
                className='checkoutProduct__image'
            /> 
            </div>
            <div>
            <h1 className='checkoutProduct__title'>
                {title}
            </h1>
            {availble ? <p className='checkoutProduct__avail'>Availble</p> : <p className='checkoutProduct__unavail'>Unavailable, extra time of delivery</p>}
            <p>
                Quantity: {quantity}
            </p>
            <p>
                Price: ${price}
            </p>
            <button className='checkoutProduct__button' onClick={removeFromBasket}>Delete</button>
            </div>

        </div>
    )
}

export default CheckoutProduct
