import firebase from 'firebase'

const firebaseApp = firebase.initializeApp({
    apiKey: "AIzaSyA38kfX86xKIFYKHtEF4kcGHqRwykRoAwY",
    authDomain: "bee-site.firebaseapp.com",
    databaseURL: "https://bee-site.firebaseio.com",
    projectId: "bee-site",
    storageBucket: "bee-site.appspot.com",
    messagingSenderId: "229508092357",
    appId: "1:229508092357:web:1d1ac5c1acb8c21f3b3bac",
    measurementId: "G-E9RFFFQJ9H"
  });

  var messagesRef = firebase.database().ref('messages');

  const saveMessage = (firstName, lastName, email, address, city, zipCode, phone, items) => {
    var newMessageRef = messagesRef.push();
    newMessageRef.set({
        items,
        firstName,
        lastName,
        email,
        address,
        city,
        zipCode,
        phone,
    })
  }

  export default saveMessage;
