import React from 'react'
import saveMessage from './Firebase';
import './Order.css'
import { useStateValue } from './StateProvider';
import { Link, useHistory } from 'react-router-dom'

function Order() {
    const history = useHistory();
    const [{ basket }, dispatch] = useStateValue();

    const sendOrder = () => {

        
        var firstName = getInputValue('firstName');
        var lastName = getInputValue('lastName');
        var email = getInputValue('email');
        var address = getInputValue('address');
        var city = getInputValue('city');
        var zipCode = getInputValue('zipCode');
        var phone = getInputValue('phone');
        var items = '';
        basket.map(i => (
                items += i.title + ': ' + i.quantity + 'qty, ' 
        ))
        
        dispatch({
            type: 'CLEAR_BASKET'
        })

        if((firstName||lastName||email||address||city||zipCode||phone).length<=0)
        {
            document.querySelector('.order__address__left__error').style.display = 'block';
        }
        else
        {
            document.querySelector('.order__alert').style.display = 'flex';
            document.querySelector('.order__block').style.display = 'block';
            saveMessage(firstName, lastName, email, address, city, zipCode, phone, items);
        }
    }

    const getInputValue = (id) => {
        return document.getElementById(id).value;
    }

    return (
        <div className='order'>
            <div className='order__block'></div>
            <div className='order__alert'>
                <p>Your items has been ordered!</p>
                <Link to='/store'><button className='order__alert__ok'>OK</button></Link>
            </div>
            <div className='order__address'>
                <div className='order__address__left'>
                    <h1>Delivery adress</h1>
                    <hr className='order__separator'/>
                    <p className='order__address__left__error'>Some fields are empty!</p>
                    <label>First name: </label>
                    <input type='text' id='firstName'/>
                    <label>Last name: </label>
                    <input type='text' id='lastName'/>
                    <label>E-mail: </label>
                    <input type='email' id='email'/>
                    <label>Address: </label>
                    <input type='text' id='address'/>
                    <label>City: </label>
                    <input type='text' id='city'/>
                    <label>Zip code: </label>
                    <input type='text' id='zipCode'/>
                    <label>Mobile phone: </label>
                    <input type='tel' id='phone'/>
                    <button onClick={sendOrder}>Order</button>
                </div>
                <div className='order__address__right'>
                    <form>
                        {/* <div className='order__address__right__inpost'>
                            <label>Inpost</label> 
                            <input type='radio' name='delivery'/>
                        </div>
                        <div className='order__address__right__courier'>
                            <label>Courier</label>
                            <input type='radio' name='delivery'/> 
                        </div> */}
                    </form>
                </div>
            </div>
            
        </div>
    )
}

export default Order
