import React from 'react'
import { useStateValue } from './StateProvider'

function CheckoutProduct({id, title, image, price, availble}) {

    const [, dispatch] = useStateValue();

    const removeFromBasket = () => {
        dispatch({
            type: 'REMOVE_FROM_BUSKET',
            item: id
        })
    }

    return (
        <div className='checkoutProduct'>
            <img
                src={image}
                alt=''
                className='checkoutProduct__image'
            />
            <h1 className='checkoutProduct__title'>
                {title}
            </h1>
            {availble ? <p className='checkoutProduct__avail'>Availble</p> : <p className='checkoutProduct__unavail'>Unavailable</p>}
            <p>
                Price: ${price}
            </p>
            <button className='details__button' onClick={removeFromBasket}>Add To Basket</button>
        </div>
    )
}

export default CheckoutProduct
