import React from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import { useStateValue } from "./StateProvider";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";

function Header() {
  const [{ basket }] = useStateValue();
  let items = basket.reduce(
    (a, b) => parseInt(a, 10) + parseInt(b.quantity, 10),
    0
  );
  return (
    <nav className="header">
      <Link to="/store/frames" className="header__link">
        <p className="header__option">Store</p>
      </Link>

      <Link to="/" className="header__link">
        <img
          className="header__logo"
          src="https://logodownload.org/wp-content/uploads/2019/07/mini-logo.png"
          alt=""
        />
      </Link>

      <Link to="/contact" className="header__link">
        <p className="header__option">Contact</p>
      </Link>

      <div className="header__basket">
        {items !== 0 ? (
          <Link to="/checkout" className="header__link">
            <div className="header__option">
              <ShoppingBasketIcon />
              <span className="header__basketCounter">{items}</span>
            </div>
          </Link>
        ) : (
          <Link className="header__link">
            <div className="header__basket">
              <ShoppingBasketIcon />
              <span className="header__basketCounter">{items}</span>
            </div>
          </Link>
        )}
      </div>
    </nav>
  );
}

export default Header;
