import React from 'react'
import saveMessage from './Firebase';
import './Order.css'
import { useStateValue } from './StateProvider';

function Order() {

    const [{ basket }] = useStateValue();

    const sendOrder = () => {
        
        var firstName = getInputValue('firstName');
        var lastName = getInputValue('lastName');
        var email = getInputValue('email');
        var address = getInputValue('address');
        var city = getInputValue('city');
        var zipCode = getInputValue('zipCode');
        var phone = getInputValue('phone');
        var items = '';
        basket.map(i => (
                items += i.title + ': ' + i.quantity + 'qty, ' 
        ))
        saveMessage(firstName, lastName, email, address, city, zipCode, phone, items);
    }

    const getInputValue = (id) => {
        return document.getElementById(id).value;
    }

    return (
        <div className='order'>
            <div className='order__address'>
                <h1>Delivery adress</h1>
                <hr className='order__separator'/>
                <label>First name: </label>
                <input type='text' id='firstName'/>
                <label>Last name: </label>
                <input type='text' id='lastName'/>
                <label>E-mail: </label>
                <input type='text' id='email'/>
                <label>Address: </label>
                <input type='text' id='address'/>
                <label>City: </label>
                <input type='text' id='city'/>
                <label>Zip code: </label>
                <input type='text' id='zipCode'/>
                <label>Mobile phone: </label>
                <input type='text' id='phone'/>
                <button onClick={sendOrder}>Order</button>
            </div>
        </div>
    )
}

export default Order
