export const initialState = {
    id: '',
    title: 'lol',
    image: '',
    price: 0
}

function reducer(state, action) {
    switch(action.type){
        case 'SEND_PRODUCT':
            return {
                ...state,
                id: action.id,
                title: action.title,
                image: action.image,
                price: action.price
            }
        default:
            return state;
    }
        
}

export default reducer;
