import React, { useState } from 'react'
import { useStateValue } from './StateProvider';
import './ProductDetails.css'

function ProductDetails() {

    const [{ details }] = useStateValue();
    const [multiplier, increaseMuliplier] = useState(1);

    return (
        <div className='details'>
            {details.map(item => (
              <div className='details__card'>
              <img
                  className='details__img'
                  src={item.image}
                  alt=''
              />
              <div className='details__description'>
                <h3 className='details__title'>
                    {item.title}
                </h3>
                <label>Quantity</label>
                <input className='details__quantity' onChange={event => increaseMuliplier(event.target.value)} type='number' name='Quantity' />
                <p className='details__price'>
                    Price: ${item.price*multiplier}
                </p>
                <button className='details__button'>Add To Basket</button>
              </div>
          </div>
              ))}
        </div>
    )
}

export default ProductDetails
